# -*- coding: utf-8 -*- import re   import json import os string=input() dict={}   #字典 list=[]   #列表  dif=0 if string[:2]=="1!":    dif=1 elif string[:2]=="2!":    dif=2 else:    dif=3 string=string[2:]  dict['姓名']=re.search('[^,]+',string).group()  #提取名字 string=re.sub('.+,','',string)    #删除字符串中的名字  dict['手机']=re.search('\d{11}',string).group()    #提取手机号码 string=re.sub('\d{11}','',string)     #删除字符串中的号码  string=string.replace(".","",1)  flag=re.search('.+?省',string)   #省 if str=='北京'or str=='天津'or str=='上海'or str=='重庆':     list.append(str) elif flag!=None:       str=re.search('.+?省',string).group()      string=string.replace(str,'',1)     list.append(str) else:           str=re.search('..',string).group()     string=string.replace(str,'',1)     list.append(str+'省')  flag=re.search('.+?市', string)  #市 if flag != None :     str = re.search('.+?市', string).group()     string = string.replace(str, '', 1)     list.append(str) else:     str = re.search('..', string).group()     string = string.replace(str,'', 1)     list.append(str+'市')  flag = re.search('.+?(?:市|县|区)', string)  #县/区 if flag != None :     str = re.search('.+?(?:市|县|区)', string).group()     string = string.replace(str, '', 1)     list.append(str) else:     string = string.replace(str, '', 1)     list.append('')  flag = re.search('..+?(?:镇|街道|乡)', string)  #镇/街道/乡 if flag != None :     str = re.search('..+?(?:镇|街道|乡)', string).group()     string = string.replace(str, '', 1)     list.append(str) else:     list.append('')  if dif==1:     flag = re.search('[^\.]+', string)  #具体地址     if flag != None :         str = re.search('[^\.]+', string).group()         list.append(str)     else:         list.append('')  elif dif==2:     flag = re.search('.+?(?:路|巷|街|道|乡|胡同)', string)  #路/巷/街     if flag != None :        str = re.search('.+?(?:路|巷|街|道|乡|胡同)', string).group()        string = string.replace(str, '', 1)        list.append(str)     else:        list.append('')      flag = re.search('.+?(?:号)', string)  #号     if (flag != None) :        str = re.search('.+?(?:号)', string).group()        string = string.replace(str, '', 1)        list.append(str)     else:        list.append('')      flag = re.search('[^\.]+', string)  #具体地址     if flag != None :        str = re.search('[^\.]+', string).group()        list.append(str)     else:        list.append('')  elif dif==3:     pass  dict['地址'] = list print(json.dumps(dict)) 